            new MyClass { Colors = new List<string> { "Red", "Orange", "Violet" } }
        };

        // The color we're looking for
        string searchColor = "Red";

        // Using LINQ to find MyClass objects whose Colors contain "Red"
        var itemsWithRed = myClassList.Where(m => m.Colors.Contains(searchColor)).ToList();

